import React , {Component} from 'react';

class Info extends Component {
    constructor(props){
        super(props)

        this.state = {
            amount: null
        }
    }

    handleAmountChange = (event) => {
        this.setState({ amount: event.target.value})
    }

    render() {
        return(
            <div>
                <form>
                <div>
                    <label>Amount</label>
                    <input type="number" value={this.state.amount} onChange={this.state.handleAmountChange} />

                </div>
                </form>
            </div>
            )
    }

}

export default Info;
