import React , {Component} from 'react';
import { Button } from 'reactstrap';
class Info extends Component {
    constructor(props){
        super(props)

        this.state = {
            amount: null,
            topic : null,
            comment: null,
            date : null
        }
    }

    handleAmountChange = (event) => {
        this.setState({ amount: event.target.value})
    }
    handleLabelChange = (event) => {
        this.setState({ topic: event.target.value})
    }
    handleCommentChange = (event) => {
        this.setState({ comment: event.target.value})
    }
    handleDateChange = (event) => {
        this.setState({ date: event.target.value})
    }
    handleSubmit = (event) => {
        alert(`${this.state.amount}`)
    }

    render() {
        return(
            <div>
                <form onSubmit={this.state.handleSubmit}>
                <div>
                    <label>Amount</label>
                    <input type="number" value={this.state.amount} onChange={this.state.handleAmountChange} />
                </div>
                <div>
                    <label>Label</label>
                    <select value={this.state.topic} onChange = {this.state.handleLabelChange}>
                        <option value="groccery">Groccery</option>
                        <option value="groccery">Drinks</option>
                        <option value="groccery">Food</option>
                        <option value="groccery">Shopping</option>
                        <option value="groccery">Bills</option>
                    </select>
                </div>
                <div>
                    <label>Comments</label>
                    <textarea value={this.state.comment} onChange={this.state.handleCommentChange}></textarea>
                </div>
                <div>
                    <label>Date</label>
                    <input type="date" value={this.state.date} onChange={this.state.handleDateChange} />
                </div>
                <Button color="success">success</Button>
                </form>
            </div>
            )
    }

}

export default Info;
