import React , {Component} from 'react';
import {  Card, Button, CardHeader, CardFooter, CardBody, CardTitle, CardText,Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import {db} from './firebase.js';
class Info extends Component {
	constructor(props) {
		super(props)

		this.state = {
			amount: '',
			comments: '',
			topic: 'groccery',
            date: '',
		}
	}

	handleAmountChange = event => {
		this.setState({
			amount: event.target.value
		})
	}

	handleCommentsChange = event => {
		this.setState({
			comments: event.target.value
		})
	}

	handleTopicChange = event => {
		this.setState({
			topic: event.target.value
		})
	}
    handleDateChange = event => {
		this.setState({
			date: event.target.value
		})
		
	}


	handleSubmit = event => {
		const [UserData, setUserData] = useState([]);

  useEffect(() => {
    db.collection("UserData").where('user','==','TestUser').where('date','==',).onSnapshot((snapshot) => {
      setUserData(snapshot.docs.map((doc) => doc.data()));
    });
  }, []);

		db.collection("UserData")
.doc()
.update({
amount: this.state.amount,
comments: this.state.comments,
topic: this.state.topic,
date: this.state.date,
user:'TestUser'
})
.then(function () {

})
.catch(function (error) {
alert(error);
});
		// alert(`${this.state.amount} ${this.state.comments} ${this.state.topic} ${this.state.date}`)
		event.preventDefault()
	}

	render() {
		const { amount, comments, topic ,date} = this.state
		return (
                <Card color='secondary' style={{width:"20rem",height:"28rem"}}>
					<CardBody>
			<Form onSubmit={this.handleSubmit}>
				<FormGroup>
					<Label>Amount</Label>
					<Input
						type="text"
						value={amount}
						onChange={this.handleAmountChange}
					/>
				</FormGroup>
				<FormGroup>
					<Label>Topic</Label>
					<Input type="select" value={topic} onChange={this.handleTopicChange}>
						<option value="groccery">Groccery</option>
						<option value="shopping">Shopping</option>
						<option value="bills">Bills</option>
                        <option value="food">Food</option>
						<option value="drinks">Drinks</option>
                    </Input>
				</FormGroup>
                <FormGroup>
                    
					<Label>Date</Label>
				<Input type='Date' onChange={this.handleDateChange} value={date}></Input>
                </FormGroup>
                <FormGroup>
					<Label>Comments</Label>
					<Input type='textarea'
						value={comments}
						onChange={this.handleCommentsChange}
					/>
				</FormGroup>
				<Button color="success" type="submit">Submit</Button>
			</Form>
			</CardBody>
            </Card>
		)
	}
}

export default Info;
